declare enum Network {
    mainnet = "mainnet",
    testnet = "testnet",
    regtest = "regtest"
}
declare enum AddressType {
    p2pkh = "p2pkh",
    p2sh = "p2sh",
    p2wpkh = "p2wpkh",
    p2wsh = "p2wsh"
}
declare type AddressInfo = {
    bech32: boolean;
    network: Network;
    address: string;
    type: AddressType;
};
declare const getAddressInfo: (address: string) => AddressInfo;
declare const validate: (address: string, network?: Network | undefined) => boolean;
export { getAddressInfo, Network, AddressInfo, validate };
export default validate;
